import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Navigate, useNavigate } from 'react-router-dom';
import { CopyToClipboard } from 'react-copy-to-clipboard';



function PlayingGame(props){

  const { roomId }= useParams();

  const [player,setPlayer] = useState(0);

  const [roomData, setRoomData] = useState(null);

  const [ready, setReady]  = useState(false);
  const [gameCreated, setgameCreated] = useState(undefined);
  const [lastplayedCard, setlastplayedCard] = useState(0);

  const [isCopied, setIsCopied] = useState(false);

  const port = 'localhost:3000/game/'
  let navigate = useNavigate();





  const fetchRoomData = async (roomId) => {
    return fetch(`http://localhost:3001/game/data/${roomId}`)
    .then(response => {
      if (!response.ok){
        throw new Error(`HTTP error! status : ${response.status}`);
      }
      return response.json();
    })  
    .catch(e => {
      console.error('There was a problem with your fetch operation ' + e.message);
    });
  }

  const addReadyPerson = async (roomId) => {
    return fetch(`http://localhost:3001/game/ready/${roomId}`, {
      method: 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
    });
  }

  const removeNumberFromArray = async (roomId, arrayName, numberToRemove) => {
    return fetch(`http://localhost:3001/game/data/${roomId}`, {
        method: 'PATCH',
        headers: {
            'Content-Type' : 'application/json',
        },
        body: JSON.stringify({ arrayName, numberToRemove }),
    });
}

const startGame = async (roomId) => {
  return fetch(`http://localhost:3001/game/create/${roomId}`, {
    method: 'POST',
    headers: {
      'Content-Type' : 'application/json',
    },
  });
}



  useEffect(() => {
    if (localStorage.userID === 'first'){
      setPlayer(0);
      localStorage.setItem(player,0);
    }
    else{
      setPlayer(1);
      localStorage.setItem(player,1)
    }
  })


  useEffect(() =>  {
    const intervalID = setInterval(() => {
      fetchRoomData(roomId).then(setRoomData)
    }, 100);

    return () => clearInterval(intervalID);
  }, [roomId]);


  function handleClickReady(){
    localStorage.setItem('player',player)
    setReady(true);
    addReadyPerson(roomId);
  }



  function handelPlayAgain(){
    startGame(roomId);
  }

  function handleCopyClick() {
    setIsCopied(true);
    setTimeout(() => setIsCopied(false), 2000)
  }








return (

  <div> 

    <div>

    {roomData ? (
      <>

    {roomData.lost && <div>
        <h1>FAILURE</h1>

        {localStorage.clear()}
        <button onClick={handelPlayAgain}> Play again? </button>

    </div>}

    {roomData.won && !roomData.lost && <div>
        <h1>You Win!</h1>
        {localStorage.clear()}
        <button onClick={handelPlayAgain}> Play again? </button>
    </div>}

    {!roomData.won && !roomData.lost &&
    <div>
    <h1>Game in progress...</h1>



    <div>

      <CopyToClipboard text={`${port}${roomId}`} onCopy={handleCopyClick}>
        <button>
        <h2>localhost:3000/game/3r2Rd</h2>
        Copy Link to Clipboard
        </button>
      </CopyToClipboard>
      {isCopied && <span style={{color: 'red'}}>Copied!</span>}
    </div>

    <h2> Game Room : {roomId}</h2>

    {!ready && !roomData.gameStarted && <button onClick={handleClickReady}>Ready</button>}




      {roomData.gameStarted && <div>

      <div>
        <h2>Last Playted Card : {String(roomData.lastPlayedCard)}</h2>
      </div>

     {player === 0 &&  <div>
        <h2>Numbers 1:</h2>
        {roomData.numbers1.map((num, index) => (
            <button key={index} onClick={() => removeNumberFromArray(roomId, 'numbers1', num)}>
                {num}
            </button>
        ))}
        </div> }

     {player === 1 && <div>
        <h2>Numbers 2:</h2>
        {roomData.numbers2.map((num, index) => (
            <button key={index} onClick={() => removeNumberFromArray(roomId, 'numbers2', num)}>
                {num}
            </button>
        ))}
        </div>}

        </div>}




      </div>}


      </>

    ) : (
      <div>Loading...</div>
    )}




  </div>


</div>

);
}
export default PlayingGame;
