import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";



function PlayingGame(props){

  const { roomId }= useParams();
  const [userID,setuserID] = useState(localStorage.getItem('userID'));
  const [currID,setcurrID] = useState('');
  const [player,setPlayer] = useState(0);

  const [roomData, setRoomData] = useState(null);
  const [hasUserID, sethasUserID] = useState(false);

  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [ready, setReady]  = useState(false);
  const [roomCreated, setRoomCreated] = useState(false);
  const [gameStatus, setGameStatus] = useState(null);


  function createRoom(roomId) {
    setRoomCreated(true);

    return fetch(`http://localhost:3001/game/create/${roomId}`, {
      method: 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
    });
  }


  const fetchRoomData = async (roomId) => {
    return fetch(`http://localhost:3001/game/data/${roomId}`)
    .then(response => {
      if (!response.ok){
        throw new Error(`HTTP error! status : ${response.status}`);
      }
      return response.json();
    })  
    .catch(e => {
      console.error('There was a problem with your fetch operation ' + e.message);
    });
  }

  const addReadyPerson = async (roomId) => {
    return fetch(`http://localhost:3001/game/ready/${roomId}`, {
      method: 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
    });
  }


  useEffect(() => {

    if (userID != null){
      sethasUserID(true);
    }
  })


  useEffect(() =>  {
    const intervalID = setInterval(() => {
      fetchRoomData(roomId).then(setRoomData)
      .then(setGameStatus(String(roomData.gameStarted)));
    }, 2000);

    console.log(gameStatus);
    return () => clearInterval(intervalID);
  }, [roomId]);


  function handleClickReady(){
    localStorage.setItem('player',player)
    setReady(true);
    addReadyPerson(roomId);
  }


  function handelSumbit(event){
    setPlayer(1);
    event.preventDefault();
    setuserID(currID);
    localStorage.setItem('userID',currID);
    sethasUserID(true);

  }


return (
  <div> 
    <h1>In the Game Now</h1>
    <h2> Game Room : {roomId}</h2>
    <h2> User ID : {userID} </h2>
    <h2>Player : {player}</h2>

    {!ready && <button onClick={handleClickReady}>Ready</button>}


    {!hasUserID && <div>
      <h2> Select an ID</h2>

      <form onSubmit = {handelSumbit}>
      <input
      type = "text"
      value = {userID}
      onChange = {event => setcurrID(event.target.value)}     
      />
      <button type="submit">Done</button>
    </form>
    </div>}





    {roomData ? (
      <>
        <h2>Numbers 1: {roomData.numbers1.join(', ')}</h2>
        <h2>Numbers 2: {roomData.numbers2.join(', ')}</h2>
        <h2>Lost: {String(roomData.lost)}</h2>
        <h2>Won: {String(roomData.won)}</h2>
        <h2>Status :  {String(roomData.gameStarted)}</h2>
      </>
    ) : (
      <div>Loading room data...</div>
    )}

   <div> 
    { !roomCreated &&  
    < button onClick={() => createRoom(roomId)}>Create the new Room.</button> }

    </div>

  </div>

);
}
export default PlayingGame;
