import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";



function PlayingGame(props){

  const { roomId }= useParams();
  const userID = localStorage.getItem('userID');
  console.log(roomId);
  console.log(userID);



  const [roomData, setRoomData] = useState(null);


  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [ready, setReady]  = useState(false);
  const [roomCreated, setRoomCreated] = useState(false);


  function createRoom(roomId) {
    return fetch(`http://localhost:3001/game/create/${roomId}`, {
      method: 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
    });
  }


  const createRoomm = async () => {
    setRoomCreated(true);
    if (roomId) {
      try {
        const response = await fetch(`/game/create/${roomId}`, {
          method: 'POST',
        });

        if (response.ok) {
          console.log('Room created successfully');
        } else {
          console.error('Failed to create room');
        }
      } catch (error) {
        console.error('Error creating room:', error);
      }
    }
  };


  const fetchRoomData = async (roomId) => {
    return fetch(`http://localhost:3001/game/data/${roomId}`)
    .then(response => {
      if (!response.ok){
        throw new Error(`HTTP error! status : ${response.status}`);
      }
      return response.json();
    })  
    .catch(e => {
      console.error('There was a problem with your fetch operation ' + e.message);
    });
  }

  const addReadyPerson = async (roomId) => {
    return fetch(`http://localhost:3001/game/ready/${roomId}`)
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status : ${response.status}`);
      }
    })  
    .catch(e => {
      console.error('There was a problem with your fetch operation ' + e.message);
    });
  }


  useEffect(() =>  {
    console.log(`Room ID : ${roomId}`);
    const intervalID = setInterval(() => {
      fetchRoomData(roomId).then(setRoomData);
    }, 500);
    return () => clearInterval(intervalID);
  }, [roomId]);


  function handleClickReady(){
    setReady(true);


  }



return (
  <div> 
    <h1>In the Game Now</h1>
    <h2> Game Room : {roomId}</h2>
    <h2> User ID : {userID} </h2>

    <button onClick={handleClickReady}>Ready</button>





    {roomData ? (
      <>
        <h2>Numbers 1: {roomData.numbers1.join(', ')}</h2>
        <h2>Numbers 2: {roomData.numbers2.join(', ')}</h2>
        <h2>Lost: {String(roomData.lost)}</h2>
        <h2>Won: {String(roomData.won)}</h2>
      </>
    ) : (
      <div>Loading room data...</div>
    )}



   <div> 
    { !roomCreated && <div> 

    < button onClick={() => createRoom(roomId)}>Create the new Room.</button>
    </div> }

    </div>

  </div>

);
}
export default PlayingGame;
