import React from "react";
import ReadyButton from "./ReadyButton";
import Countdown from "./Countdown";
import CopyLink from "./CopyLink";
import Level from "./Level";

function BeforeGameStarts({
  roomData,
  handleCopyClick,
  isCopied,
  ready,
  show1,
  show2,
  show3,
  handleClickReady,
}) {
  if (!roomData.won && !roomData.lost && !ready && !roomData.gameStarted) {
    return <ReadyButton handleClickReady={handleClickReady} />;
  }

  if (!roomData.won && !roomData.lost) {
    return <Level level={roomData.level} />;
  }

  return (
    <>

      <Level roomData={roomData} />
      <CopyLink
        roomData={roomData}
        gameStarted={roomData.gameStarted}
        handleCopyClick={handleCopyClick}
        isCopied={isCopied}
      />
      <Countdown
        won={roomData.won}
        lost={roomData.lost}
        gameStarted={roomData.gameStarted}
        show1={show1}
        show2={show2}
        show3={show3}
      />
    </>
  );
}
export default BeforeGameStarts;
