import React, { useEffect, useState } from "react";
import { json, useParams } from "react-router-dom";
import { Navigate, useNavigate, useLocation } from 'react-router-dom';
import classes from './PlayingGame.module.css';

import Countdown from "./Countdown";
import ReadyButton from './ReadyButton';
import CopyLink from './CopyLink';
import LostPage from './LostPage';
import WonPage from "./WonPage";
import Level from "./Level";
import InGameRendering from "./InGameRendering";
import BeforeGameStarts from "./BeforeGameStarts";

function PlayingGame(props){

  const { roomId }= useParams();
  const [playerID , setPlayerID] = useState(0);
  const [ready, setReady]  = useState(false);
  const [isCopied, setIsCopied] = useState(false);
  const [numberOfPlayers, setnumberOfPlayers] = useState(false);
  const [roomData, setRoomData] = useState(null);
  const location  = useLocation();
  const [didCountDown, setdidCountDown] = useState(false);

  const [show1, setshow1] = useState(false);
  const [show2, setshow2] = useState(false);
  const [show3, setshow3] = useState(false);

  const frontport = process.env.REACT_APP_FRONTPORT || 'http://localhost/3000/';
  const backport =  process.env.REACT_APP_BACKPORT || 'http://localhost/3001'

  let navigate = useNavigate();


  const fetchRoomData = async (roomId ) => {
    try {
      const response = await fetch(`${backport}game/data/${roomId}`)
      if (!response.ok){
        throw new Error(`HTTP error! status : ${response.status}`);
      }
      return await response.json();
    } catch (e) {
      console.error('There was a problem with your fetch operation ' + e.message);
      throw e;
    }
  };

  const addReadyPerson = async (roomId) => {
    try {
      const response = await fetch(`${backport}game/ready/${roomId}`, {
        method: 'POST',
        headers: {
          'Content-Type' : 'application/json',
        },
      }).catch(e => console.error('Error:', e));
    if(!response.ok){
      throw new Error(`HTTP error! Status : ${response}`);
    }
    const responseData = await response.json();
    const varPlayerID = responseData.readyPlayersCount;
    setPlayerID(varPlayerID - 1);

    }catch(e){
      console.error('Error:', e);
      throw e;
    }
  }


  useEffect(() => {
    console.log("player ID: ");
    console.log(playerID);
}, [playerID]);



  const removeNumberFromArray = async (roomId, playerId, numberToRemove) => {
    return fetch(`${backport}game/data/${roomId}`, {
        method: 'PATCH',
        headers: {
            'Content-Type' : 'application/json',
        },
        body: JSON.stringify({ playerId, numberToRemove }),
    });
  }

const setLevel = async (roomId, level) => {
  return fetch(`${backport}game/resetLevel/${roomId}`, {
    method: 'PATCH',
    headers: {
        'Content-Type' : 'application/json',
    },
    body: JSON.stringify({ level }),
});
}


const startGame = async (roomId, numberOfPlayers) => {
  return fetch(`${backport}game/create/${roomId}`, {
    method: 'POST',
    headers: {
      'Content-Type' : 'application/json',
    },
    body: JSON.stringify({ numberOfPlayers }),
  });
}

const resetReadyStatus = async (roomId) => {
  return fetch(`${backport}/game/resetReady/${roomId}`, {
    method: 'POST',
    headers: {
      'Content-Type' : 'application/json',
    },
  });
}

useEffect(() =>  {
  const fetchData = async () => {
      try {
        const data = await fetchRoomData(roomId);
        setRoomData(data);
        if (data.playersReady === data.playerCount){
          if (!didCountDown) {
            countDown();
            setdidCountDown(true);
          }
        }
      }
      catch (error) {
        console.log(error("Error fetching room data:" , error));
      }
    };
    const intervalID = setInterval(fetchData, 120);
    return () => clearInterval(intervalID);
  },[roomId,didCountDown]);

  function handleClickReady(){
    addReadyPerson(roomId);
    setReady(true);
    setdidCountDown(false);
  }

  async function handelPlayAgain(){
    setLevel(roomId,1);
    startGame(roomId, numberOfPlayers);
  }

  async function handelContinue(){
    setLevel(roomId, roomData.level + 1);
    startGame(roomId, numberOfPlayers); 
  }

  function handleCopyClick() {
    setIsCopied(true);
    setTimeout(() => setIsCopied(false), 2000);
  }

  function countDown(){
    setshow3(true);
    setTimeout(() => {
      setshow3(false);
      setshow2(true);
    }, 1000);
    setTimeout(() => {
      setshow2(false);
      setshow1(true);
    }, 2000);
    setTimeout(() => {
      setshow1(false);
    }, 3000);
    setTimeout(() => setReady(false), 4000);
    setTimeout(() => setIsCopied(false), 5000);
    setdidCountDown(true);
  }



return (

  <div> 
    <div>
    {roomData ? (
    <>
    <LostPage lost = {roomData.lost} level = {roomData.level} handelPlayAgain = {handelPlayAgain}/>
    <WonPage won = {roomData.won} level = {roomData.level} handelContinue = {handelContinue}/>

    <BeforeGameStarts won = {roomData.won} lost = {roomData.lost} />
                      level = {roomData.level} 
                      gameStarted = {roomData.gameStarted}
                      handleCopyClick = {handleCopyClick} 
                      isCopied = {isCopied}  
                      ready = {ready} 
                      handleClickReady = {handleClickReady}
                      show1={show1} show2={show2} show3={show3}/> 

    <Level  won = {roomData.won} lost = {roomData.lost} level = {roomData.level} /> 
    <CopyLink won = {roomData.won} lost = {roomData.lost}  gameStarted = {roomData.gameStarted} handleCopyClick = {handleCopyClick} isCopied = {isCopied} />
    <ReadyButton won = {roomData.won} lost = {roomData.lost}  ready = {ready} gameStarted = {roomData.gameStarted} handleClickReady = {handleClickReady}/>
    <Countdown won = {roomData.won} lost = {roomData.lost}  gameStarted={roomData.gameStarted} show1={show1} show2={show2} show3={show3} />

    <InGameRendering roomData = {roomData} show1 = {show1} show2 = {show2} show3 = {show3} removeNumberFromArray = {removeNumberFromArray} playerID = {playerID} roomId={roomId} />
      </>
    ) : (
      <div>Loading...</div>
    )}
  </div>
</div>

);
}
export default PlayingGame;
