import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";



function PlayingGame(props){

  const { roomId }= useParams();
  const userID = localStorage.getItem('userID');
  console.log(roomId);
  console.log(userID);



  const [roomData, setRoomData] = useState(null);


  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');


  const createRoom = async () => {
    if (roomId) {
      await fetch(`/game/create/${roomId}`, {
        method: 'POST',
      });
    }
  }


  const fetchRoomData = async (roomId) => {
    try {
      const response = await fetch(`/game/data/${roomId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching room data:', error);
      return null;
    }
  }












  function fetchMessages(roomId) {
    return fetch(`http://localhost:3001/message/${roomId}`)
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .catch(e => {
      console.error('There was a problem with your fetch operation: ' + e.message);
    });
  }

  function sendMessages(roomId, message) {
    return fetch(`http://localhost:3001/message/${roomId}`, {
      method: 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
      body: JSON.stringify({text: message}),
    });
  }

  function deleteRoom(roomId) {
    return fetch(`http://localhost:3001/message/${roomId}`, {
      method: 'DELETE',
    });
  }

  useEffect(() =>  {

    console.log(`Room ID : ${roomId}`);
    const intervalID = setInterval(() => {
      fetchMessages(roomId).then(setMessages);
    }, 500);

    return () => clearInterval(intervalID);
  }, [roomId]);

  const handelSumbit = (event) => {
    event.preventDefault();
    sendMessages(roomId, newMessage)
    .then(() => setNewMessage(''))
    .then(() => fetchMessages(roomId).then(setMessages));
  };




return (
  <div> 
    <h1>In the Game Now</h1>
    <h2> Chat Room : {roomId}</h2>
    <h2> User ID : {userID} </h2>
    <h2> Cards : </h2>
    {Array.isArray(messages) && messages.map((message, index) => (
  <p key = {index}>{message.text}</p>
))}



    <button onClick={() => createRoom(roomId)}>Create the new Room.</button>


  </div>

);
}
export default PlayingGame;
