import { React, useState } from 'react';
import PropTypes from 'prop-types';
import classes from './LostPage.module.css';

function LostPage({ level, handelPlayAgain, playedCards }) {
  const [showCards, setshowCards] = useState(true);
  if (playedCards === undefined) {
    setshowCards(false);
  }

  return (
    <div className={classes.failureDiv}>
      <h1 className={classes.failure}>YOU LOSE</h1>
      {showCards && (
        <h2>
          Cards played :
          {String(playedCards)}
        </h2>
      )}

      <h2>
        Level
        {level}
      </h2>
      <button type= "button" className={classes.playAgain} onClick={handelPlayAgain}>
        Play again?
      </button>
    </div>
  );
}

LostPage.propTypes = {
  level: PropTypes.number.isRequired,
  handelPlayAgain: PropTypes.func.isRequired,
  playedCards: PropTypes.number.isRequired,
};

export default LostPage;
